    def choosePdf(self):
        get_filenames_path, ok = QFileDialog.getOpenFileNames(self, '选择文件', '', 'PDF files(*.pdf)')
        if ok:
            self.lineEdit.setText(str(' '.join(get_filenames_path)))

    def pdf2Img(self):
        pdfPath = self.lineEdit.text()
        if pdfPath == '':
            QtWidgets.QMessageBox.warning(self.pushButton, "标题", '请先选择PDF文件地址')
        else:
            QtWidgets.QMessageBox.information(self.pushButton, "标题", self.lineEdit.text())
